CREATE TABLE S_LST_OF_VAL (
	ROW_ID VARCHAR2(15 CHAR) NOT NULL,
	PAR_ROW_ID VARCHAR2(15 CHAR),
	CREATED TIMESTAMP DEFAULT sysdate,
	CREATED_BY VARCHAR2(15 CHAR),
	LAST_UPD DATE DEFAULT sysdate,
	LAST_UPD_BY VARCHAR2(15 CHAR),
	ACTIVE_FLG CHAR(1 CHAR) DEFAULT 'Y' NOT NULL,
	NAME VARCHAR2(50 CHAR),
	RPLCTN_LVL_CD VARCHAR2(30 CHAR) DEFAULT 'All' NOT NULL,
	TYPE VARCHAR2(30 CHAR),
	VAL VARCHAR2(30 CHAR),
	MODIFIABLE_FLG CHAR(1 CHAR) DEFAULT 'Y',
	DB_LAST_UPD TIMESTAMP DEFAULT SYSDATE,
	ORDER_BY NUMBER(22, 7),
	SUB_TYPE VARCHAR2(30 CHAR),
	
	CONSTRAINT pk_row_id_lst_of_val PRIMARY KEY (ROW_ID),
	CONSTRAINT fk_par_row_id_lst_of_val FOREIGN KEY (PAR_ROW_ID) REFERENCES S_LST_OF_VAL (ROW_ID)
);
	
